<?php/** * 抽象控制類別 * */class GoEz_Controller{    /**     * Config     *     * @var array     */    protected $_config = array();    /**     * 指定 Config     *     * @param array $config     */    public function setConfig($config)    {        $this->_config = $config;    }    /**     * 取得 Config     *     * @return array     */    public function getConfig()    {        return $this->_config;    }    /**     * Request     *     * @var GoEz_Request     */    protected $_request = null;    /**     * 指定 Request     *     * @param GoEz_Request $request     */    public function setRequest(GoEz_Request $request)    {        $this->_request = $request;    }    /**     * 取得 Request     *     * @return GoEz_Request     */    public function getRequest()    {        return $this->_request;    }    /**     * View     *     * @var GoEz_View     */    protected $_view = null;    /**     * 指定 View     *     * @param GoEz_View $view     */    public function setView(GoEz_View $view)    {        $this->_view = $view;    }    /**     * 取得 View     *     * @return GoEz_View     */    public function getView()    {        return $this->_view;    }    /**     * 控制器初始化     *     */    public function init() {}    /**     * Dispatch 之前     *     */    public function beforeDispatch() {}    /**     * Dispatch 之後     *     */    public function afterDispatch() {}    /**     * 預設動作     *     */    public function indexAction() {}    /**     * 頁面重導向     *     * @param string $url     */    public function redirectTo($url)    {        if (!preg_match('/^https?:\/\//', $url)) {            $url = $this->_baseUrl . '/' . ltrim($url, '/');        }        header('Location: ' . $url);    }}