<?php/** * Request * */class GoEz_Request{    /**     * Base Url     *     * @var string     */    protected $_baseUrl = '';    /**     * 初始化     *     */    public function __construct()    {        $this->_init();    }    /**     * 初始化 BaseUrl     *     */    protected function _init()    {        $this->_baseUrl = rtrim(str_replace('index.php', '', $_SERVER['SCRIPT_NAME']), '/');    }    /**     * 取得 BaseUrl     *     * @return string     */    public function getBaseUrl()    {        return $this->_baseUrl;    }    /**     * 是否為 Post     *     * @return bool     */    public static function isPost()    {        return (bool) ('POST' == $_SERVER['REQUEST_METHOD']);    }    /**     * 取得 POST 值     *     * @param string $key     * @return string     */    public static function getPost($key, $stripTags = true)    {        return isset($_POST[$key]) ? ($stripTags ? strip_tags(trim($_POST[$key])) : trim($_POST[$key])) : null;    }    /**     * 取得 GET 值     *     * @param string $key     * @return string     */    public static function getQuery($key, $stripTags = true)    {        return isset($_GET[$key]) ? ($stripTags ? strip_tags(trim($_GET[$key])) : trim($_GET[$key])) : null;    }}